For.DomContent:

//depends: $tabLabel
var UNSELECTED_IMG = 'data:image/png;base64,';
var SELECTED_IMG = 'data:image/png;base64,';

var items = $tabLabel.value.split(/\|/);
var specs = [];

for (var i in items) {
  var label = items[i];
  var selected = false;

  if (label.match(/^#/)) {
    selected = true;
    label = label.substring(1);
  }

  specs.push({
    _name: "div",
    _uri: "http://www.w3.org/1999/xhtml",
    style: new CSS()
        .set("-moz-border-image",
          "url(" + (selected ? SELECTED_IMG : UNSELECTED_IMG) + ") 0 5 0 5 stretch stretch")
        .set("display", "inline-block")
        .set("width", (100 / items.length).toFixed(1) + "%")
        .set("position", "relative")
        .set("border", 0)
        .set("padding", 0)
        .set("margin", 0)
        .set("-moz-box-sizing", "border-box"),

    _children: [{
      _name: "div",
      _uri: "http://www.w3.org/1999/xhtml",
      style: new CSS()
          .set("-moz-border-image",
            "url(" + (selected ? SELECTED_IMG : UNSELECTED_IMG) + ") 0 5 0 5 stretch stretch")
          .set("position", "absolute")
          .set("margin", "0 -2px")
          .set("border-top", 0)
          .set("border-bottom", 0)
          .set("padding", 0)
          .set("height", "96px")
          .set("width", "100%")
          .set("z-index", selected ? "1000" : "999"),
      _children: [{
        _text: label,
        style: new CSS()
            .set("position", "absolute")
            .set("width", "100%")
            .set("top", "60px")
            .set("text-align", "center")
            .set("line-height", "21px")
            .set("height", "1em")
            .set("overflow", "hidden")
            .set("color", selected ? "#ffffff" : "#808080")
            .set("font-family", "'Droid Sans'")
            .set("font-size", "21px")
      }]
    }]
  });
}

var fragment = null;
try {
  fragment = F.newDOMFragment(specs);
} catch (e) {
  Console.dumpError(e);
}
fragment;

Action.Impl:

var b = this.getBounding();
return [
  new SnappingData("WidgetBottom", b.y + b.height , "Top", false, this.id)
];

